import * as React from "react";
import styled from "styled-components";
import * as Icons from "../icons";

const Wrap = styled.div`
  position: relative;
`;
const Img = styled.img`
  width: 100%;
  display: block;
`;
const Icon = styled.span`
  position: absolute;
  top: 0px;
  right: 0px;
`;

export function GalleryItem({ imagePath, icon, id_pos }) {
  //handle cho comment modal
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const IconComp = Icons[icon];

  //State cho fetch detailpost
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [postdetail, setItems] = useState([]);
  const headers = {
    "x-access-token": localStorage.getItem("token"),
  };
  //Lay api detail
  useEffect(() => {
    fetch("http://localhost:8000/api/post/" + id_post, { headers })
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);
  //console.log("imagePath - GalleryItem", imagePath);
  return (
    <Wrap>
      <Img alt="gallery-post" src={imagePath} />
      {/* <Icon>
        {icon && <IconComp />}
        { <span className="media-icon"></span> }
      </Icon> */}
       {/*Modal popup comment*/}
       <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <CommentModal
            accountName={postdetail.posts?.Users?.name}
            storyBorder={storyBorder}
            image={imagePath}
            hours={hours}
            id_post={id_post}
            id_user={postdetail.posts?.id_user}
            comments={postdetail.posts?.Comments}
            userName={postdetail.posts?.Users?.name}
            profileIcon={profileIcon}
          />
        </Box>
      </Modal>
    </Wrap>


  );
}
GalleryItem.defaultProps = {
  imagePath: "/images/transparent.png",
  icon: "",
};
