    //   );

    return (
      <div className="container">
        <Sticky topOffset={-80}>
          <div className="sidebar">
            <Profile
              username={this.state.items.user.name}
              caption="Aleksandar Popović"
              urlText="Switch"
              iconSize="big"
              image={image}
            />
            <Suggestions />
            <Footer />
          </div>
        </Sticky>
        {/* {items.map((item) => (
