import { data } from "./data";
import * as Icons from "../icons";

const Stories = styled.div`
  display: flex;
  justify-content: start;
  padding: 0 24px;
  overflow-x: scroll;
  position: relative;
  margin-bottom: 44px;
  background: var(--ins-background-primary);
  @media only screen and (max-width: 735px) {
    padding: 0;
    margin-bottom: 21px;
