import "../styles/commentmodal.scss";
import Card from "./Card";
import React, { useState, useEffect } from "react";
import Box from "@mui/material/Box";
import Modal from "@mui/material/Modal";
import Profile from "./Profile";
import { ReactComponent as CardButton } from "../images/cardButton.svg";
import CardMenu from "./CardMenu";
import Comment from "./Comment";
import ListItem from "@mui/material/ListItem";
import { FixedSizeList } from "react-window";
import AutoSizerProps from "react-virtualized-auto-sizer";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemText from "@mui/material/ListItemText";
import Carousel from "react-bootstrap/Carousel";
import Button from "@mui/material/Button";
import { ReactComponent as Notifications } from "../images/notifications.svg";
import { ReactComponent as Notifications2 } from "../images/notifications2.svg";
import { ReactComponent as Comments } from "../images/comments.svg";
import List from "@mui/material/List";
import ListSubheader from "@mui/material/ListSubheader";
import "../styles/cardMenu.scss";
// function renderRow(props) {
//   const { data, index, style } = props;
//   console.log(data.comment);
//   return (
//     <ListItem style={style} key={index} component="div" disablePadding>
//       <ListItemButton>
//         <ListItemText primary={`Item ${index + 1}`} />
//         {console.log(index)}
//       </ListItemButton>
//     </ListItem>
//   );
// }
function CommentModal(props) {
  const {
    storyBorder,
    image,
    comments,
    likedByText,
    likedByNumber,
    hours,
    userName,
    profileIcon,
    id_post,
    id_user,
    isLiked,
    countLiked,
    postLiked,
    parentCallback,
  } = props;
  const styleLike = {
    position: "fixed",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 450,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };
  const headers = {
    "x-access-token": localStorage.getItem("token"),
  };
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);

  //handle cho like modal
  const [openLike, setOpenLike] = React.useState(false);
  // const handleOpenLike = () => setOpenLike(true);
  const handleOpenLike = () => setOpenLike(true);
  const handleCloseLike = () => setOpenLike(false);
  //State cho xu ly check like
  const [isLike, setIsLike] = useState(false);
  //let countlike = postdetail?.post?.countLike;
  const ccc = countLiked;
  const check = isLiked;
  const [countLike, setCLike] = useState(ccc);

  //State cho fetch get all like
  const [postLike, setLike] = useState([]);

  useEffect(() => {
    setCLike(ccc);
  }, [check]);
  useEffect(() => {
    setIsLike(check);
  }, [check]);
  const [isLike2, setIsLike2] = useState(true);
  //setIsLike(postdetail?.post?.isLike);
  //lay like post
  useEffect(() => {
    fetch("http://localhost:8000/api/like/post/" + id_post, { headers })
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setLike(result);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, [id_post, isLike]);
  const HandleLike = () => {
    setIsLike2((prevLike) => !prevLike);
    parentCallback();
    //setIsLike(check);
    // useEffect(() => {
    //   const requestOptions = {
    //     method: "POST",
    //     headers: { headers },
    //   };
    //   fetch("https://reqres.in/api/posts" + id_post, requestOptions)
    //     .then((response) => response.json())
    //     .then((data) => setIsLike(data.like));

    // }, []);
    fetch("http://localhost:8000/api/like/post/" + id_post, {
      method: "POST",
      headers: headers,
    })
      .then((res) => res.json())
      .then((data) => setIsLike(data.like))
      .catch((err) => console.log("hey"));
    // setItems({
    //   ...postdetail,
    //   isLike: isLike,
    // });
    if (isLike) setCLike(countLike - 1);
    else setCLike(countLike + 1);
  };
  console.log("comments", comments);
  //call back
  return (
    <div className="cardModal">
      <div className="cardcontainer">
        <div className="img1">
          <Carousel interval={null}>
            {image.map((img) => {
              return (
                <Carousel.Item>
                  <img
                    className="cardImageModal"
                    src={img.url}
                    alt="card content"
                  />
                </Carousel.Item>
              );
            })}
          </Carousel>
        </div>
        <div className="menuModal">
          <header>
            <Profile
              iconSize="medium"
              storyBorder={storyBorder}
              username={userName}
              image={profileIcon}
              id_user={id_user}
            />
            <CardButton className="cardButton" />
          </header>
          <div className="commentsModal">
            {comments?.map((comment) => {
              return (
                <FixedSizeList
                  height={200}
                  width={360}
                  itemSize={10}
                  itemCount={comments.length}
                  overscanCount={5}
                >
                  {console.log("item count",comment.length)}
                  <Comment
                    key={comment.id_com}
                    accountName={comment.User.name}
                    comment={comment.content}
                  />
                </FixedSizeList>
              );
            })}
          </div>
          {/* <div className="comments">

          </div> */}

          {/* {isLike2 ? (
            <Button onClick={HandleLike}>
              {isLike ? (
                <Notifications2 className="icon" />
              ) : (
                <Notifications className="icon" />
              )}
            </Button>
          ) : (
            <Button onClick={HandleLike}>
              {isLike ? (
                <Notifications2 className="icon" />
              ) : (
                <Notifications className="icon" />
              )}
            </Button>
          )} */}
          <div className="cardMenu">
            <div className="interactions">
              {/* <Button onClick={() => setIsLike((prevLike) => !prevLike)}>
            {isLike ? (
              <Notifications2 className="icon" />
            ) : (
              <Notifications className="icon" />
            )}
          </Button> */}
              {
                <Button onClick={HandleLike}>
                  {isLike ? (
                    <Notifications2 className="icon" />
                  ) : (
                    <Notifications className="icon" />
                  )}
                </Button>
              }
              <Button>
                <Comments className="icon" />
              </Button>

              {/* <Inbox className="icon" /> */}
            </div>
            {/* <Bookmark className="icon" /> */}
          </div>
          <div className="likedBy">
            <span>
              <Button
                variant="text"
                size="small"
                sx={{
                  marginBottom: 0.2,
                }}
                onClick={handleOpenLike}
              >
                Liked By
              </Button>{" "}
              <strong>{countLike}</strong>
            </span>
          </div>
          <div className="timePosted">{hours} HOURS AGO</div>
          {/* <div className="addComment">
            <div className="commentText">Add a comment...</div>
            <div className="postText">Post</div>
          </div> */}
          <div className="post__comment">
            <form className="form">
              <div className="addComment">
                {/* <div className="commentText">
                  <input
                    text="textarea"
                    className="post__modalcommentbox"
                    placeholder="Add a comment..."
                  />
                </div> */}
                <div className="commentText">
                  <textarea
                    className="post__modalcommentbox"
                    name="caption"
                    rows="4"
                    cols="50"
                  ></textarea>
                </div>
                {/* <div className="postText">Post</div> */}
                <button className="post_comment">Đăng</button>
              </div>
            </form>
          </div>
        </div>
      </div>
      {/*Modal popup like*/}
      <Modal
        open={openLike}
        onClose={handleCloseLike}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={styleLike}>
          <List
            sx={{
              width: "100%",
              maxWidth: 360,
              bgcolor: "background.paper",
              position: "relative",
              overflow: "auto",
              maxHeight: 300,
              "& ul": { padding: 0 },
            }}
            subheader={<li />}
          >
            <li key={`likedby`}>
              <ul>
                <ListSubheader>{`Liked By`}</ListSubheader>
                {postLike?.users?.map((user) => {
                  return (
                    <Profile
                      iconSize="medium"
                      storyBorder={true}
                      username={user.User.name}
                      image={user.User.image}
                      id_user={user.User.id_user}
                    />
                  );
                })}
              </ul>
            </li>
          </List>
        </Box>
      </Modal>
    </div>
  );
}

export default CommentModal;
