import "../styles/navigation.scss";
import Menu from "./Menu";
import logo from "../images/instagramLogo.png";
//import searchIcon from "../images/searchIcon.png";
import axios from "axios";
import React, { useState, useEffect } from "react";
import { TextField } from "@mui/material";
import Box from '@mui/material/Box';
import Popper from '@mui/material/Popper';

function Navigation(props) {
  const { input } = props;
  const [text, setText] = useState("");
  const [user, setUser] = useState([]);
  //state pop over
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(anchorEl ? null : event.currentTarget);
  };

  const openPop = Boolean(anchorEl);
  const id = openPop ? "simple-popper" : undefined;
  //
  const handleChangeInput = (event) => {
    setAnchorEl(anchorEl ? null : event.currentTarget);
    setText(event.target.value);
    axios
      .get("http://localhost:8000/api/user/search?q=" + text, {
        headers: {
          "x-access-token": localStorage.getItem("token"),
        },
      })
      .then((res) => {
        setUser(res.data.users);
        console.log(user);
      });
  };
  return (
    <div className="navigation">
      <div className="container">
        <img className="logo" src={logo} alt="instagram logo" />
        <div className="search">
          <input aria-describedby={id} onChange={handleChangeInput} type="text" value={text} style={{width}} />
          <Popper id={id} open={openPop} anchorEl={anchorEl} className="search">
            <Box sx={{ border: 1, p: 1, bgcolor: "background.paper" }}>
              The content of the Popper.
            </Box>
          </Popper>
        </div>
        <Menu />
      </div>
    </div>
  );
}

export default Navigation;
