import "../styles/menu.scss";
import { ReactComponent as Home } from "../images/home.svg";
import { ReactComponent as Inbox } from "../images/inbox.svg";
import { ReactComponent as Explore } from "../images/explore.svg";
import { ReactComponent as Notifications } from "../images/notifications.svg";
import { ReactComponent as Upload } from "../images/upload.svg";
import ProfileIcon from "./ProfileIcon";
import image from "../images/profile.jpg";
import React, { useState } from "react";
import Box from "@mui/material/Box";
import Modal from "@mui/material/Modal";
import UploadModal from "./uploadModal";
import { useNavigate } from "react-router-dom";

function Menu() {
  const style = {
    position: "fixed",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 900,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };
  //state call back
  const [isCall, setCallBack] = useState(false);
  const [isCallCom, setCallBackCom] = useState(false);

  //open modal
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const navigate = useNavigate();
  const navigateHome = () => {
    // ðŸ‘‡ï¸ navigate to /
    navigate("/Home");
  };
  const saved = localStorage.getItem("info");
  const userData = JSON.parse(saved);

  //call back
  const callback = () => {
    setCallBack(!isCall);
    setOpen(false);
    // do something with value in parent component, like save to state
  };
  const callbackComment = (e) => {
    setCallBack(!isCall);
    // do something with value in parent component, like save to state
  };
  return (
    <div className="menu">
      <Home className="icon" onClick={navigateHome} />
      {/* <Inbox className="icon" />
      <Explore className="icon" /> */}
      <Notifications className="icon" />
      <Upload className="icon" onClick={handleOpen} />
      <ProfileIcon
        iconSize="small"
        image={userData?.user?.image}
        id_user={userData?.user?.id_user}
      />
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <UploadModal parentCallback={callback}></UploadModal>
        </Box>
      </Modal>
    </div>
  );
}

export default Menu;
